import React, {useState} from 'react';
import './App.css';

function App() {
  const [number, setNumber] = useState(1);

  return (
    <div className="App">
      <h1>Root : {number}</h1>
      <div className='grid'>
        <Left1 number={number} />
        <Right1 
          onIncrease = {() => {
            setNumber(number + 1)
          }}
        />
      </div>
    </div>
  );
}

export default App;
